import React, { useState, useEffect } from 'react';

function Quiz() {
  const [questions, setQuestions] = useState([]);
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [userAnswers, setUserAnswers] = useState([]);
  const [countdown, setCountdown] = useState(30);
  const [showResult, setShowResult] = useState(false);

  useEffect(() => {
    // Hier die Funktion zum Laden der Fragen
    async function fetchQuestions() {
      try {
        const response = await fetch('https://jsonplaceholder.typicode.com/posts');
        const data = await response.json();
        const slicedData = data.slice(0, 10); // Wähle die ersten 10 Fragen aus dem API-Daten
        const formattedQuestions = slicedData.map(question => ({
          title: question.body,
          options: [`A) ${question.title.split(" ")[0]}`, `B) ${question.title.split(" ")[1]}`, `C) ${question.title.split(" ")[2]}`, `D) ${question.title.split(" ")[3]}`], // Beispieloptionen
          correctAnswer: `C) ${question.title.split(" ")[2]}` // Beispiel korrekte Antwort
        }));
        setQuestions(formattedQuestions);
      } catch (error) {
        console.error('Fetch error:', error);
      }
    }

    fetchQuestions();
  }, []);

  useEffect(() => {
    if (countdown > 0) {
      const timer = setTimeout(() => {
        setCountdown(countdown - 1);
      }, 1000);

      return () => clearTimeout(timer);
    } else {
      handleNextQuestion();
    }
  }, [countdown]);

  const handleOptionClick = (selectedOption) => {
    if (countdown <= 20) {
      const selectedAnswer = questions[currentQuestionIndex].options[selectedOption];
      setUserAnswers([...userAnswers, { question: questions[currentQuestionIndex].title, answer: selectedAnswer }]);
      handleNextQuestion();
    }
  };

  const handleNextQuestion = () => {
    setCurrentQuestionIndex(currentQuestionIndex + 1);
    setCountdown(30);
    if (currentQuestionIndex + 1 === questions.length) {
      setShowResult(true);
    }
  };

  const renderOptions = () => {
    if (currentQuestionIndex < questions.length) {
      return questions[currentQuestionIndex].options.map((option, index) => (
        <div key={index} onClick={() => handleOptionClick(index)}>
          {option}
        </div>
      ));
    }
  };

  const renderResult = () => {
    return (
      <div>
        <h2>Result</h2>
        <table>
          <thead>
            <tr>
              <th>Questions</th>
              <th>Answers</th>
            </tr>
          </thead>
          <tbody>
            {userAnswers.map((answer, index) => (
              <tr key={index}>
                <td>{answer.question}</td>
                <td>{answer.answer}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    );
  };

  return (
    <div>
      {showResult ? (
        renderResult()
      ) : (
        <div>
          <h2>{questions[currentQuestionIndex]?.title}</h2>
          <div>Countdown: {countdown}</div>
          <div>{renderOptions()}</div>
        </div>
      )}
    </div>
  );
}

export default Quiz;
